import React from 'react';
import styles from './index.css';

const FileInput = ({setInputRef, onClick, label, name, hasValue, ...props}) =>
  <span className={styles.container} onClick={onClick}>
  {hasValue ? (
    <svg className={styles.icon} fill="#000" width="1.4em" height="1.4em" viewBox="0 0 24 24"><path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z"/><path d="M0 0h24v24H0z" fill="none"/></svg>
  ) : (
    <svg className={styles.icon} fill="#000" width="1.4em" height="1.4em" viewBox="0 0 433.5 433.5">
      <polygon points="140.25,331.5 293.25,331.5 293.25,178.5 395.25,178.5 216.75,0 38.25,178.5 140.25,178.5   "/>
      <rect x="38.25" y="382.5" width="357" height="51"/>
    </svg>
  )}
  <div>
    <div className={hasValue ? styles.label+" "+styles.hasValue : styles.label}>{label}</div>
    {hasValue && <div className={styles.name}>{name || "Uploaded file"}</div>}
  </div>
    <input ref={setInputRef} className={styles.hidden} type="file" {...props} />
  </span>

export default FileInput;
