import React from 'react';
import { connect } from 'react-redux';
import Select from '../components/Select';
import { updateFormValue } from '../store/form';

class PositionSelect extends React.PureComponent {
  options = null;
  handleChange = ({target}) => {
    this.props.updateFormValue("step", "number", target.value)
  }
  makeOptions = () => {
    let options = [];
    for (var i = 1; i < this.props.steps + 2; i++) {
      options.push({value: i, label: "Number "+i})
    }
    console.log("options", options);
    this.options = options
  }
  render(){
    if(!this.options){
      this.makeOptions();
    }

    return(
      <Select {...this.props} label="Number" options={this.options} onChange={this.handleChange} />
    )
  }
}

export default connect(
  ({form, procedure}, props) => ({value: form.step ? form.step.values.number : '', steps: procedure.steps ? procedure.steps.length : 0, error: form.step && form.step.inputErrors && form.step.inputErrors.number })
)(PositionSelect);
