import React from 'react';
import ProcedureImage from '../ProcedureImage';
import Placeholder from '../Placeholder';
import styles from './index.module.css';

const ProcedureImages = ({images, removeImage}) => images && images.length ? (
  <div className={styles.list}>
    {images.map(image => <ProcedureImage key={image.id} onClick={() => {removeImage(image.id)}} image={image.src} />)}
  </div>
) : (
  <Placeholder
    text="This procedure currently has no images"
    icon={<svg className={styles.icon} width="7.6em" height="7.6em" viewBox="0 0 24 24"><circle cx="12" cy="12" r="3.2"/><path d="M9 2L7.17 4H4c-1.1 0-2 .9-2 2v12c0 1.1.9 2 2 2h16c1.1 0 2-.9 2-2V6c0-1.1-.9-2-2-2h-3.17L15 2H9zm3 15c-2.76 0-5-2.24-5-5s2.24-5 5-5 5 2.24 5 5-2.24 5-5 5z"/><path d="M0 0h24v24H0z" fill="none"/></svg>}
  />
)

export default ProcedureImages;
