import { connect } from 'react-redux'
import ProcedureSteps from '../components/ProcedureSteps';

class ProcedureStepsContainer extends React.PureComponent {
  dragging = null;
  draggingOver = null
  handleDragStart = (e, number) => {
    e.dataTransfer.setData("text/html", e.parentNode);
    e.dataTransfer.setDragImage(e.parentNode, 20, 20);
    this.dragging = number;
  }
  handleDragEnd = (number) => {
    if(this.dragging && this.draggingOver && this.dragging !== this.draggingOver){

    }
    this.dragging = null;
    this.draggingOver = null;
  }
  handleDragOver = (number) => {
    this.draggingOver = number
  }
  render(){
    return(
      <ProcedureSteps steps={procedure.steps} onDragOver={this.handleDragOver} onDragStart={this.handleDragStart} onDragEnd={this.handleDragEnd} />
    )
  }
}

export default connect(
  ({procedure}) => ({steps: procedure.steps})
)(ProcedureStepsContainer)
