import React from 'react';
import { connect } from 'react-redux';
import Select from '../components/Select';
import { updateFormValue } from '../store/form';

const OPTIONS = [{value: "1", label: "1 second"}, {value: "2", label: "2 seconds"}, {value: "3", label: "3 seconds"}, {value: "4", label: "4 seconds"}, {value: "5", label: "5 seconds"}, {value: "6", label: "6 seconds"}, {value: "7", label: "7 seconds"}, {value: "8", label: "8 seconds"}]

class DurationSelect extends React.PureComponent {
  handleChange = ({target}) => {
    this.props.updateFormValue("step", "time", target.value)
  }
  render(){
    return(
      <Select {...this.props} label="Time*" options={OPTIONS} onChange={this.handleChange} />
    )
  }
}

export default connect(
  ({form, procedure}, props) => ({value: form.step ? form.step.values.time : '', steps: procedure.steps ? procedure.steps.length : 0, error: form.step && form.step.inputErrors && form.step.inputErrors.time }),
  { updateFormValue }
)(DurationSelect);
