import { takeEvery, put, call, select } from 'redux-saga/effects';
import { setFormErrors } from '../store/form';
import {
  LOGIN_REQUEST,
  LOGIN_REQUEST__SUCCESS,
  LOGIN_REQUEST__FAILURE
} from '../store/user';

function* loginSaga({payload}){
  try {
    if(payload.email && payload.email.length && payload.password && payload.password.length){
      console.log("LOGIN ACTION PAYLOAD", payload);
      //let response = yield call(API.post, '/login', {email: payload.email, password: payload.password})
      yield put({type: LOGIN_REQUEST__SUCCESS, payload: {email: payload.email}})
      localStorage.setItem('user', JSON.stringify({email: payload.email}))
    } else {
      yield put({type: LOGIN_REQUEST__FAILURE})
    }
  } catch (e) {
    console.log("ERROR", e);
    yield put({type: LOGIN_REQUEST__FAILURE})
  }
}
export default function* userSagas() {
  yield takeEvery(LOGIN_REQUEST, loginSaga);
}
