import React from 'react';
import { connect } from 'react-redux';
import FileInput from '../components/FileInput';
import { updateFormValue } from '../store/form';

class FileInputContainer extends React.PureComponent {
  inputRef = null;
  name = null;
  onChange = ({target}) => {
    this.name = target.files[0].name;
    this.props.updateFormValue(this.props.form, this.props.name, target.files[0]);
    var reader  = new FileReader();
    var that = this;
    reader.addEventListener("load", function () {
      //that.props.addImage(reader.result);
    }, false);
    reader.readAsDataURL(target.files[0]);
  }
  onClick = () => {
    if(this.props.hasValue){
      this.name = null;
      this.props.updateFormValue(this.props.name, null);
    } else {
      this.inputRef.click();
    }
  }
  setInputRef = (e) => {
    this.inputRef = e;
  }
  render(){
    let { updateFormValue, ...rest } = this.props;
    return(
      <FileInput {...rest} onChange={this.onChange} hasValue={this.props.hasValue} name={this.name} onClick={this.onClick} setInputRef={this.setInputRef} />
    )
  }
}

export default connect(
  ({form}, {name}) => ({error: form.inputErrors[name], hasValue: form.values[name] ? true : false}),
  { updateFormValue }
)(FileInputContainer);
