export const ADD_IMAGE = "ADD_IMAGE";

export const addImage = (image) => ({type: ADD_IMAGE, payload: image});

const initialState = [];
export default function(previousState = initialState, { type, payload }){
  switch (type) {
    case ADD_IMAGE:
      return [...previousState, payload]
    default:
      return previousState;
  }
}
